sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   //Modified the if to return if the newValue == oldValue to avoid
	// unnecessary trips to the server

// 	if (isLoading || newValue === '' || newValue == oldValue) {
    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {

		return;
   }

try{
	// Instantiate the GetEmailAddress Script Include
	var getEmailAddr = new GlideAjax('GetEmailAddress');
	// Specify the getEmail method
	getEmailAddr.addParam('sysparm_name','getEmail');
	// Pass the Requested for sys_id
	//Error should be sysparm_userID instead of sysparm_UserID. How do you log parameter?
	//getEmailAddr.addParam('sysparm_UserID', g_form.getValue('u_requested_for'));
	getEmailAddr.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
	// Send the requestd to the server, Also specify callback function populateEmailField
	getEmailAddr.getXML(populateEmailField);

	jslog("This message is from jslog().");
	//jslog("The value of sys_parm_name = " + g_form.getValue('sysparm_name'));
	jslog("Short Description = " + g_form.getValue('short_description'));
	jslog("the Requested for user is " + g_form.getValue('u_requested_for'));
	//How do you log u_requested_for user name? GlideRecord?
	jslog("The userID is " + g_form.getValue('u_requested_for.name'));
	jslog("The curent logged in user is " + g_user.getFullName() + ".");
	jslog("The NeedIt State value = " + g_form.getValue('state'));
	jslog("the value of sysparm_UserID is " + 'sysparm_userID.value');
}
	// When the response is back from the server
	catch(err){
		jslog('A JavaScript runtime error occured: ' +err.message);
	}


	function populateEmailField(response){
		// Extract the email address from the response, clear any value from the email field, 
		// set new value in the email field
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',emailFromScriptInclude);
	}
}
